mapper.select:
SELECT

FROM
app_update_version
WHERE 1=1

 AND app_update_version.TYPE = #{appUpdateVersion.type,jdbcType=INTEGER}


  AND app_update_version.CLIENT = #{appUpdateVersion.client,jdbcType=VARCHAR}


  AND app_update_version.VERSION_CODE = #{appUpdateVersion.versionCode,jdbcType=VARCHAR}


  AND app_update_version.VERSION_URL = #{appUpdateVersion.versionUrl,jdbcType=VARCHAR}


  AND app_update_version.SORT = #{appUpdateVersion.sort,jdbcType=INTEGER}


  AND app_update_version.UPDATE_CONTENT = #{appUpdateVersion.updateContent,jdbcType=VARCHAR}


  AND app_update_version.FORCE_TAG = #{appUpdateVersion.forceTag,jdbcType=INTEGER}


  AND app_update_version.ENABLED = #{appUpdateVersion.enabled,jdbcType=INTEGER}


  AND app_update_version.VERSION = #{appUpdateVersion.version,jdbcType=INTEGER}


  AND app_update_version.STATUS = #{appUpdateVersion.status,jdbcType=INTEGER}


  AND app_update_version.REMARK = #{appUpdateVersion.remark,jdbcType=VARCHAR}


  AND app_update_version.MEMO = #{appUpdateVersion.memo,jdbcType=VARCHAR}


  AND app_update_version.CREATE_BY = #{appUpdateVersion.createBy,jdbcType=VARCHAR}


  AND app_update_version.CREATE_DT = #{appUpdateVersion.createDt,jdbcType=VARCHAR}


  AND app_update_version.UPDATE_BY = #{appUpdateVersion.updateBy,jdbcType=VARCHAR}


  AND app_update_version.UPDATE_DT = #{appUpdateVersion.updateDt,jdbcType=VARCHAR}
